import React, { useState, useEffect } from 'react';
import { useParams } from "react-router-dom";
import MovieCard from './MovieCard';

function DirectorPage({ directors, onDirectorDelete, onMovieDelete, onMovieEdit }) {
    const [selectedDirector, id, setSelectedDirector] = useState({
        name: "",
        movies: []
    });
    const params = useParams();

    const movieCardStyle = {
        backgroundColor: "grey",
        padding: 80,
        borderRadius: '5%',
        textDecoration: 'none',
        listStyle: 'none'
    }

    useEffect(() => {
        const director = directors.find((director) => director.id === parseInt(params.id))
        if (director) {
            setSelectedDirector(director)
        }
    }, [directors]);

    function handleDeleteClick() {
        fetch(`http://localhost:9292/directors/${id}`, {
            method: "DELETE",
        });
        onDirectorDelete(id)
    }

    const getMovies = selectedDirector.movies.map((movie) => {
        return <div className="card" style={movieCardStyle}>
                    <MovieCard id={movie.id}
                               name={movie.name}
                               runtime={movie.runtime}
                               genre={movie.genre}
                               imageUrl={movie.imageUrl}
                               onMovieDelete={onMovieDelete}
                               onMovieEdit={onMovieEdit}
                    />
                </div>
    })

    return (
        <div>
            <h1>
                {selectedDirector.name} Movies:
            </h1>
            <button onClick={handleDeleteClick}>
                <span role="img" aria-label="delete">
                    ðŸ—‘
                </span>
            </button>
            <ul>
                {getMovies}
            </ul>
        </div>
    )
}

export default DirectorPage;
