import React, { useState } from 'react';
import MovieCard from './MovieCard';
import NewMovie from './NewMovie';
import DirectorPage from './DirectorPage';

function Directors({ directors, onMovieEdit, onMovieDelete, onAddItem }) {
    const [movies, setMovies] = useState([]);

    function handleNewMovie(newMovie) {
        setMovies([...movies, newMovie])
      }

    const getDirectors = directors.map((director) => {
        return <div>
                    <h1 key={director.id}>{director.name}</h1>
                    <button>Director Page</button>
               </div>
    })
    return (
        <div>
            <h1>Directors</h1>
            <button>Add New Director</button>
            {getDirectors}
        </div>
    )
}

export default Directors;
