import React, { useState } from 'react';
import { BrowserRouter as  Link } from "react-router-dom";
import MovieCard from './MovieCard';
import NewMovie from './NewMovie';
import DirectorPage from './DirectorPage';

function Directors({ directors, onMovieEdit, onMovieDelete, onAddItem }) {

    const getDirectors = directors.map((director) => {
        return <div key={director.id}>
                    <h1>{director.name}</h1>
                    <Link to={`/directors/${director.id}`} >Director Page</Link>
               </div>
    })
    return (
        <div>
            <h1>All Directors</h1>
            <button>Add New Director</button>
            {getDirectors}
        </div>
    )
}

export default Directors;
