import React, { useState, useEffect } from 'react';
import { useParams } from "react-router-dom";
import MovieCard from './MovieCard';
import NewMovie from './NewMovie';

function DirectorPage({ directors }) {
    const [movies, setMovies] = useState([]);
    const [selectedDirector, setSelectedDirector] = useState({
        name: "",
        movies: []
    });
    const params = useParams();

    const movieCardStyle = {
        backgroundColor: "grey",
        padding: 80,
        borderRadius: '5%',
        textDecoration: 'none',
        listStyle: 'none'
    }

    function handleNewMovie(newMovie) {
        setMovies([...movies, newMovie])
      }

      function handleDeleteMovie(id) {
        const updatedMovies = movies.filter((movie) => movie.id !== id);
        setMovies(updatedMovies);
      }

      function handleEditedMovies(updatedMovieObj) {
        const updatedMovies = movies.map((movie) => {
          if (movie.id === updatedMovieObj.id) {
            return updatedMovieObj;
          } else {
            return movie;
          }
        });
        setMovies(updatedMovies);
      }

    useEffect(() => {
        const director = directors.find((director) => director.id === parseInt(params.id))
        if (director) {
            setSelectedDirector(director)
        }
    }, [directors]);

    const getMovies = selectedDirector.movies.map((movie) => {
        return <div className="card" style={movieCardStyle}>
                    <MovieCard id={movie.id}
                               name={movie.name}
                               runtime={movie.runtime}
                               genre={movie.genre}
                               imageUrl={movie.imageUrl}
                               onMovieDelete={handleDeleteMovie}
                               onMovieEdit={handleEditedMovies}
                    />
                </div>
    })

    return (
        <div>
            <h1>
                {selectedDirector.name} Movies:
            </h1>
            <ul>
                {getMovies}
            </ul>
        </div>
    )
}

export default DirectorPage;
