import React, { useState, useEffet} from 'react';
import MovieCard from './MovieCard';

function DirectorsPage({ directors, onMovieEdit, onMovieDelete }) {

    function handleDeleteClick() {
        fetch(`http://localhost:9292/directors/${id}/movies`, {
            method: "DELETE",
        });
        onMovieDelete(id);
    }

    function handleUpdateMovie(updatedMovie) {
        setEditing(false);
        onMovieEdit(updatedMovie);
    }

    const getDirectors = directors.map((director) => {
        return <div>
                    <h1>{director.name}</h1>
                    {director.movies.map((movie) => {
                        return <MovieCard 
                               id={movie.id}
                               name={movie.name}
                               runtime={movie.runtime}
                               genre={movie.genre}
                               imageUrl={movie.imageUrl}
                               onMovieDelete={onMovieDelete}
                               onMovieEdit={onMovieEdit}
                        />
                    })}
               </div>
    })
    return (
        <div>
            Directors
            {getDirectors}
        </div>
    )
}

export default DirectorsPage;
