import React, { useEffect, useState } from "react";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import Login from "./Login";
import NewMovie from "./NewMovie";
import Directors from "./Directors";
import NewDirector from "./NewDirector";
import DirectorPage from "./DirectorPage";

function App() {
  // const [movies, setMovies] = useState([]);
  const [directors, setDirectors] = useState([]);

  const buttonStyle = {
    color: "silver",
    textDecoration: 'none',
    listStyle: 'none'
  };

  const textStyle = {
    color: "black",
    textDecoration: 'none'
  };

  // useEffect(() => {
  //   fetch("http://localhost:9292/movies")
  //   .then((res) => res.json())
  //   .then(data => setMovies(data))
  // }, []);

  useEffect(() => {
    fetch("http://localhost:9292/directors")
    .then((res) => res.json())
    .then(data => setDirectors(data))
  }, []);

function handleNewMovie(newMovie) {
  //find director involved
  //const updatedMovies = add this movie to that directors array
  //const updatedDirector = spread operater on director object 
  //const updatedDirectors = directors.map([])
  setMovies([...movies, newMovie])
}

// function handleNewMovie(newMovie) {
//   const newMovie = directors.find()
// }

function handleNewDirector(newDirector) {
  setDirectors([...directors, newDirector])
}

function handleDeleteMovie(id) {
  const updatedMovies = directors[id].movies.filter((movie) => movie.id !== id);
  setDirectors(updatedMovies);
}

function handleEditedMovies(updatedMovieObj) {
  const updatedMovies = directors.movies.map((movie) => {
    if (movie.id === updatedMovieObj.id) {
      return updatedMovieObj;
    } else {
      return movie;
    }
  });
  setDirectors([updatedMovies]);
}

    return (
      <div>
        <Router>
      <div>
        <nav>
          <ul>
            <button>
              <li style={buttonStyle}>
                <Link to="/" style={textStyle}>Login</Link>
              </li>
            </button>
            <button>
              <li style={buttonStyle}>
                <Link to="/directors" style={textStyle}>Directors</Link>
              </li>
            </button>
          </ul>
        </nav>
        <Switch>
          <Route exact path="/">
            <Login />
          </Route>
          <Route exact path="/directors">
            <NewDirector onAddItem={handleNewDirector} />
            <Directors 
              directors={directors} 
              />
          </Route>
          <Route exact path="/directors/:id">
            <NewMovie 
            //onAddItem={handleNewMovie} 
            />
            <DirectorPage 
              directors={directors}
              onMovieDelete={handleDeleteMovie}
              onMovieEdit={handleEditedMovies}
              />
          </Route>
        </Switch>
      </div>
    </Router>
    </div>
    );
  }

  export default App;
